
CREATE TABLE IF NOT EXISTS ssh_keys (
    id INT AUTO_INCREMENT PRIMARY KEY,
	host_name VARCHAR(255),
	pub_key TEXT,
	last_renewal TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
	valid_through TIMESTAMP DEFAULT 0
) ENGINE=INNODB;

